import { Component } from '@angular/core';
import { IotService } from 'src/app/services/iot.service';
import { Subject } from 'rxjs';
import { debounceTime } from 'rxjs/operators';

@Component({
  selector: 'app-control',
  templateUrl: './control.component.html'
})
export class ControlComponent {

  deshumidificador = false;
  private toggleSubject = new Subject<boolean>();

  constructor(private iot: IotService) {
    // Suscribirse al subject con debounce
    this.toggleSubject.pipe(
      debounceTime(500) // espera 500ms entre cambios
    ).subscribe((estado) => {
      const accion = estado ? 'on' : 'off';
      this.iot.setDeshumidificador(accion).subscribe({
        next: (res) => {
          console.log('Comando enviado correctamente:', res);
          alert('Comando enviado correctamente');
        },
        error: (err) => {
          console.error('Error al enviar comando:', err);
          alert('No se pudo enviar el comando al deshumidificador');
          // Revertir estado si falla
          this.deshumidificador = !estado;
        }
      });
    });
  }

  toggleDeshumidificador() {
    // Emitimos el estado al subject
    this.toggleSubject.next(this.deshumidificador);
  }
}
