import { Component, AfterViewInit, ViewChild, ElementRef } from '@angular/core';
import { Chart } from 'chart.js/auto';

@Component({
  selector: 'app-grafico-funcion',
  templateUrl: './grafico-funcion.component.html'
})
export class GraficoFuncionComponent implements AfterViewInit {

  @ViewChild('funcion') canvas!: ElementRef<HTMLCanvasElement>;

  ngAfterViewInit() {
    const ctx = this.canvas.nativeElement.getContext('2d');

    // Datos de ejemplo: cada objeto es {x, y}
    const datos = [
      {x: 1, y: 2},
      {x: 2, y: 3},
      {x: 3, y: 1},
      {x: 4, y: 4},
      {x: 5, y: 2}
    ];

    new Chart(ctx!, {
      type: 'scatter', // Puntos individuales
      data: {
        datasets: [
          {
            label: 'Mi función',
            data: datos,
            backgroundColor: 'rgba(86,69,146,0.85)',
            pointRadius: 6
          }
        ]
      },
      options: {
        scales: {
          x: {
            type: 'linear',
            position: 'bottom',
            title: { display: true, text: 'Eje X' }
          },
          y: {
            title: { display: true, text: 'Eje Y' }
          }
        }
      }
    });
  }
}
