import { Component } from '@angular/core';

@Component({
  selector: 'app-control',
  templateUrl: './control.component.html',
  styleUrls: ['./control.component.css'],
})
export class ControlComponent {
  switchVentilador = false;
  switchLuz = false;
  switchDes = false;

  toggleSwitch() {
    this.switchOn = !this.switchOn;
  }
}
