import { Component, AfterViewInit, ViewChild, ElementRef } from '@angular/core';
import { Chart } from 'chart.js/auto';

@Component({
  selector: 'app-grafico-linea',
  templateUrl: './grafico-linea.component.html',
  styleUrls: ['./grafico-linea.component.css']
})
export class GraficoLineaComponent implements AfterViewInit {

  @ViewChild('mensual') canvas!: ElementRef<HTMLCanvasElement>;

  ngAfterViewInit() {
    const ctx = this.canvas.nativeElement.getContext('2d');

    new Chart(ctx!, {
      type: 'line',
      data: {
        labels: Array.from({length: 30}, (_, i) => i + 1), // días del mes
        datasets: [
          {
            label: 'Temperatura (°C)',
            data: [22,23,21,24,25,23,22,21,23,24,25,26,22,21,22,23,24,25,23,22,21,24,23,22,24,25,23,22,21,23],
            borderColor: 'rgba(86,69,146,0.85)',
            backgroundColor: 'rgba(86,69,146,0.85)',
            showLine: false,
            pointRadius: 5
          }
        ]
      }
    });
  }
}
