import { Component } from '@angular/core';

@Component({
  selector: 'app-control',
  templateUrl: './control.component.html',
  styleUrls: ['./control.component.css'],
})
export class ControlComponent {
  switchVentilador = false;
  switchLuz = false;
  switchDes = false;

  toggleSwitchV() {
    this.switchVentilador = !this.switchVentilador;
  }

  toggleSwitchL() {
    this.switchLuz = !this.switchLuz;
  }

  toggleSwitchD() {
    this.switchDes = !this.switchDes;
  }
}
