import { Component, OnInit } from '@angular/core';
import { AlertaService } from 'src/app/services/alerta.service';

@Component({
  selector: 'app-avisos',
  templateUrl: './avisos.component.html',
  styleUrls: ['./avisos.component.css']
})
export class AvisosComponent implements OnInit {
  mensajes: string[] = [];
  maxMensajes = 10;
  storageKey = 'avisos';

  constructor(private alertService: AlertaService) {}

  ngOnInit() {
    // Cargar mensajes previos desde localStorage
    const guardados = localStorage.getItem(this.storageKey);
    if (guardados) this.mensajes = JSON.parse(guardados);

    // Suscribirse a nuevos mensajes
    this.alertService.alert$.subscribe(msg => {
      this.mensajes.push(msg);

      // Limitar cantidad
      if (this.mensajes.length > this.maxMensajes) {
        this.mensajes.shift();
      }

      // Guardar en localStorage
      localStorage.setItem(this.storageKey, JSON.stringify(this.mensajes));
    });
  }
}
