// ia.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class IaService {

  // Antes:
  // private baseUrl = '/api/ia';

  // Ahora:
  private baseUrl = 'http://localhost:3000/api/ia';

  constructor(private http: HttpClient) {}

  pedir(mensaje: string) {
    return this.http.post<{ answer: string }>(this.baseUrl, { prompt: mensaje });
  }
}
